func (o *lazyObject) init() error {
    o.lock.Lock()
    defer o.lock.Unlock()
    if o.loaded {
        return o.err
    }
    // 回顾Visitor的诞生一节，知道load() 方法即为objectLoader() 方法
    o.mapper, o.typer, o.err = o.loader() // 分析四
